import { useState } from "react";

import Nav from "./components/Nav";
import SelectOption from "./components/Selectoption";
import "./tailwind.css";
import next from "./assets/next.png"
import pervious from "./assets/back.png"
import player from "./assets/tom_brady 1.png"
function App() {
  const [rowsPerPage, setRowsPerPage] = useState(8);
  const [currentPage, setCurrentPage] = useState(1);
  const data = [
    { name: 'John Doe', team: 'Team A', position: 'Forward', salary: '$100,000', points: 50 },
    { name: 'Jane Smith', team: 'Team B', position: 'Midfielder', salary: '$80,000', points: 40 },
    { name: 'Mike Johnson', team: 'Team A', position: 'Defender', salary: '$90,000', points: 35 },
    { name: 'Mike Johnson', team: 'Team A', position: 'Defender', salary: '$90,000', points: 35 },
    { name: 'Mike Johnson', team: 'Team A', position: 'Defender', salary: '$90,000', points: 35 },
    { name: 'Mike Johnson', team: 'Team A', position: 'Defender', salary: '$90,000', points: 35 },
    { name: 'Mike Johnson', team: 'Team A', position: 'Defender', salary: '$90,000', points: 35 },
    { name: 'Mike Johnson', team: 'Team A', position: 'Defender', salary: '$90,000', points: 35 },
    { name: 'Mike Johnson', team: 'Team A', position: 'Defender', salary: '$90,000', points: 35 },
    { name: 'Mike Johnson', team: 'Team A', position: 'Defender', salary: '$90,000', points: 35 },
    { name: 'Mike Johnson', team: 'Team A', position: 'Defender', salary: '$90,000', points: 35 },
    // Add more dummy data as needed
  ];
  const indexOfLastRow = currentPage * rowsPerPage;
  const indexOfFirstRow = indexOfLastRow - rowsPerPage;
  const currentRows = data.slice(indexOfFirstRow, indexOfLastRow);

  // Change page
  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  // Change rows per page
  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 8));
    setCurrentPage(1);
  };

  return (
    <>
    <Nav/>
    <br />
    <SelectOption/>
    <br />

    <div className="flex flex-col">
            <div className="overflow-x-auto">
                <div className="p-1.5 w-full inline-block align-middle">
                    <div className="overflow-hidden border rounded-lg">
                        <table className="min-w-full table-auto  rounded-md">
        <thead className="rounded">
          <tr>
            <th className="px-6 py-3 bg-gr-700 text-left text-xs leading-4 font-medium text-white uppercase tracking-wider">
              Name
            </th>
            <th className="px-6 py-3 bg-gr-700 text-left text-xs leading-4 font-medium text-white uppercase tracking-wider">
              Team
            </th>
            <th className="px-6 py-3 bg-gr-700 text-left text-xs leading-4 font-medium text-white uppercase tracking-wider">
              Position
            </th>
            <th className="px-6 py-3 bg-gr-700 text-left text-xs leading-4 font-medium text-white uppercase tracking-wider">
              Salary
            </th>
            <th className="px-6 py-3 bg-gr-700 text-left text-xs leading-4 font-medium text-white uppercase tracking-wider">
              Points
            </th>
          </tr>
        </thead>
        <tbody className="bg-gr-800 text-white rounded-md ">
        {currentRows.map((item, index) => (
            <tr key={index} className="hover:bg-yl-200">
              <td className="px-6 py-4 whitespace-no-wrap">{item.name}</td>
              <td className="px-6 py-4 whitespace-no-wrap">{item.team}</td>
              <td className="px-6 py-4 whitespace-no-wrap">{item.position}</td>
              <td className="px-6 py-4 whitespace-no-wrap">{item.salary}</td>
              <td className="px-6 py-4 whitespace-no-wrap">{item.points}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination controls */}
      <div className="bg-gr-700 flex items-center justify-between h-12 rounded">
      <div className="flex justify-center items-center">
          <span className="text-lg leading-5 text-white">page </span>
          <select
            className="mx-2  bg-gr-900  rounded-md px-2 py-1 text-lg leading-5 text-white"
            value={rowsPerPage}
            onChange={handleRowsPerPageChange}
          >
            <option>1</option>
            <option>10</option>
            <option>20</option>
          </select>

        </div>

        <div className="flex justify-center items-center">
          <span className="text-lg leading-5 text-white">Rows per page</span>
          <select
            className="mx-2  bg-gr-900  rounded-md px-2 py-1 text-lg leading-5 text-white"
            value={rowsPerPage}
            onChange={handleRowsPerPageChange}
          >
            <option>8</option>
            <option>20</option>
            <option>30</option>
          </select>

        </div>

        <div className="">
          <span className="text-lg leading-5  text-white">
            {indexOfFirstRow + 1}-{indexOfLastRow > data.length ? data.length : indexOfLastRow} of {data.length}
          </span>

          <button
            className="px-4 py-2 rounded-md text-sm leading-5 font-medium text-white mr-2"
            disabled={currentPage === 1}
            onClick={() => handlePageChange(currentPage - 1)}
          >
            <img src={pervious}  class="h-4 w-5 justify-start " alt="pervious" />
          </button>
          <button
            className=" py-2  rounded-md text-sm leading-5 font-medium text-white"
            disabled={indexOfLastRow >= data.length}
            onClick={() => handlePageChange(currentPage + 1)}
          >
            <img src={next}  class="h-4 w-5 justify-start " alt="next" />
          </button>
        </div>
      </div>
                    </div>
                </div>
            </div>
        </div>
    </>

  );
}

export default App;
